<?phpinclude_once('commun/MyPage.php');include_once('commun/MyBdd.php');include_once('commun/MyTools.php');//define('FPDF_FONTPATH','font/');require('fpdf/fpdf.php');require_once('qrcode/qrcode.class.php');// Gestion de la Feuille de Classement par Journeeclass FeuilleCltNiveauPhase extends MyPage	 {		function FeuilleCltNiveauPhase()	{		MyPage::MyPage();		$myBdd = new MyBdd();	  		$codeCompet = utyGetSession('codeCompet', '');		$codeSaison = utyGetSaison();		$arrayCompetition = $myBdd->GetCompetition($codeCompet, $codeSaison);		$titreCompet = 'Compétition : '.$arrayCompetition['Libelle'].' ('.$codeCompet.')';		$qualif = $arrayCompetition['Qualifies'];		$elim = $arrayCompetition['Elimines'];		$sponsor = str_replace('http://www.kayak-polo.info/','',$arrayCompetition['SponsorLink']);		$logo = str_replace('http://www.kayak-polo.info/','',$arrayCompetition['LogoLink']);		//Saison		$titreDate = "Saison ".$codeSaison;		// Langue		$langue = parse_ini_file("commun/MyLang.ini", true);		if(isset($_GET['lang']) && $_GET['lang'] == 'en')			$arrayCompetition['En_actif'] = 'O';		else			$arrayCompetition['En_actif'] = '';					if($arrayCompetition['En_actif'] == 'O')			$lang = $langue['EN'];		else			$lang = $langue['FR'];		//Création		$pdf = new FPDF('P');		$pdf->Open();		$pdf->SetTitle("Classement par phase");				$pdf->SetAuthor("kayak-polo.info");		$pdf->SetCreator("kayak-polo.info");		$pdf->AddPage();		if($arrayCompetition['Sponsor_actif'] == 'O' && $sponsor != '')			$pdf->SetAutoPageBreak(true, 30);		else			$pdf->SetAutoPageBreak(true, 15);		// logo		if($arrayCompetition['Kpi_ffck_actif'] == 'O')		{			$pdf->Image('css/banniere1.jpg',10,10,0,12,'jpg',"http://www.kayak-polo.info");			$pdf->Image('img/ffck2.jpg',168,10,0,12,'jpg',"http://www.ffck.org");		}		if($arrayCompetition['Logo_actif'] == 'O' && $logo != '')  //&& file_exists($logo)		{			$size = getimagesize($logo);			$largeur=$size[0];			$hauteur=$size[1];			$ratio=20/$hauteur;	//hauteur imposée de 20mm			$newlargeur=$largeur*$ratio;			$posi=105-($newlargeur/2);	//210mm = largeur de page			$pdf->image($logo, $posi, 8, 0,20);		}		if($arrayCompetition['Sponsor_actif'] == 'O' && $sponsor != '')  //&& file_exists($sponsor)		{			$size = getimagesize($sponsor);			$largeur=$size[0];			$hauteur=$size[1];			$ratio=16/$hauteur;	//hauteur imposée de 16mm			$newlargeur=$largeur*$ratio;			$posi=105-($newlargeur/2);	//210mm = largeur de page			$pdf->image($sponsor, $posi, 267, 0,16);		}		// QRCode		$qrcode = new QRcode('http://www.kayak-polo.info/Classements.php?Compet='.$codeCompet.'&Group='.$arrayCompetition['Code_ref'].'&Saison='.$codeSaison, 'L'); // error level : L, M, Q, H		//$qrcode->displayFPDF($fpdf, $x, $y, $s, $background, $color);		$qrcode->displayFPDF($pdf, 177, 240, 24);		// titre		$pdf->Ln(22);		$pdf->SetFont('Arial','B',14);		if($arrayCompetition['Titre_actif'] == 'O')			$pdf->Cell(190,5,$arrayCompetition['Libelle'],0,1,'C');		else			$pdf->Cell(190,5,$arrayCompetition['Soustitre'],0,1,'C');//		$pdf->Ln(4);		if($arrayCompetition['Soustitre2'] != '')				$pdf->Cell(190,5,$arrayCompetition['Soustitre2'],0,1,'C');		$pdf->Ln(4);		$pdf->SetFont('Arial','BI',10);		$pdf->Cell(190,5,$lang['CLASSEMENT_PAR_PHASE'],0,0,'C');		$pdf->Ln(4);		// données		$myBdd = new MyBdd();				$sql  = "Select a.Id, a.Libelle, a.Code_club, ";		$sql .= "b.Id_journee, b.Clt_publi, b.Pts_publi, b.J_publi, b.G_publi, b.N_publi, b.P_publi, b.F_publi, b.Plus_publi, b.Moins_publi, b.Diff_publi, b.PtsNiveau_publi, b.CltNiveau_publi, ";		$sql .= "c.Phase, c.Niveau, c.Lieu, ";		$sql .= "IF(LEFT(c.Phase, 5) = 'Group' OR LEFT(c.Phase, 5) = 'Poule', c.Phase, 'Z') typePhase ";		$sql .= "From gickp_Competitions_Equipes a, ";		$sql .= "gickp_Competitions_Equipes_Journee b Join gickp_Journees c On (b.Id_journee = c.Id) ";		$sql .= "Where a.Id = b.Id ";		$sql .= "And c.Code_competition = '";		$sql .= $codeCompet;		$sql .= "' And c.Code_saison = '";		$sql .= utyGetSaison();		$sql .= "' Order By typePhase ASC, c.Niveau, c.Phase Asc, c.Date_debut Asc, c.Lieu ASC, b.Clt_publi Asc, b.Diff_publi Desc, b.Plus_publi Asc ";	 		$result = mysql_query($sql, $myBdd->m_link) or die ("Erreur Load =>    ".$sql);		$num_results = mysql_num_rows($result);		$idJournee = 0;		for ($i=0;$i<$num_results;$i++)		{			$row = mysql_fetch_array($result);				if (stristr($row['Phase'], 'group') === FALSE && stristr($row['Phase'], 'poule') === FALSE)			{				if ($row['Id_journee'] != $idJournee)				{					$idJournee = $row['Id_journee'];					$pdf->Ln(5);					$pdf->SetFont('Arial','BI',10);					$pdf->Cell(190,5,$row['Phase'], 0,1,'C');					$pdf->SetFont('Arial','B',9);					$sql2  = "Select m.Validation, m.ScoreA, m.ScoreB, ce1.Libelle EquipeA, ce2.Libelle EquipeB ";					$sql2 .= "From gickp_Matchs m left outer join gickp_Competitions_Equipes ce1 on (m.Id_equipeA = ce1.Id) left outer join gickp_Competitions_Equipes ce2 on (m.Id_equipeB = ce2.Id) ";					$sql2 .= "Where m.Id_journee = ".$row['Id_journee']." "; 					$sql2 .= "And m.Publication = 'O' "; 					$result2 = mysql_query($sql2, $myBdd->m_link) or die ("Erreur Load 2<br>".$sql2);					$num_results2 = mysql_num_rows($result2);					for ($j=0;$j<$num_results2;$j++)					{						$row2 = mysql_fetch_array($result2);						if($row2['Validation'] != 'O')						{							$pdf->SetFont('Arial','',9);							$pdf->Cell(89, 4, $row2['EquipeA'], 0, 0,'R');							$pdf->Cell(5, 4, '', 0, 0,'C');							$pdf->Cell(2, 4, '-', 0, 0,'C');							$pdf->Cell(5, 4, '', 0, 0,'C');							$pdf->Cell(89, 4, $row2['EquipeB'], 0, 1,'L');						}						else						{							if($row2['ScoreA'] > $row2['ScoreB'])								$pdf->SetFont('Arial','B',9);							else								$pdf->SetFont('Arial','',9);							$pdf->Cell(89, 4, $row2['EquipeA'], 0, 0,'R');							$pdf->Cell(5, 4, $row2['ScoreA'], 0, 0,'C');							$pdf->SetFont('Arial','',9);							$pdf->Cell(2, 4, '-', 0, 0,'C');							if($row2['ScoreA'] < $row2['ScoreB'])								$pdf->SetFont('Arial','B',9);							else								$pdf->SetFont('Arial','',9);							$pdf->Cell(5, 4, $row2['ScoreB'], 0, 0,'C');							$pdf->Cell(89, 4, $row2['EquipeB'], 0, 1,'L');						}					}				}			}			else			{				$idEquipe = $row['Id'];				if ($row['Id_journee'] != $idJournee)				{					$idJournee = $row['Id_journee'];										$pdf->Ln(5);					if($arrayCompetition['Points'] == '4-2-1-0')						$pdf->Cell(26,4, '', 0, 0,'C');					else						$pdf->Cell(30,4, '', 0, 0,'C');					$pdf->SetFont('Arial','BI',10);					$pdf->Cell(61,4,$row['Phase'], 'B', 0,'C'); //     "JOURNEE ".$codeCompet.'/'.$idJournee.'/'.					$pdf->SetFont('Arial','BI',9);					$pdf->Cell(8,4, "Pts", 'B', 0,'C');					$pdf->Cell(7,4, $lang['Joue'], 'B', 0,'C');					$pdf->Cell(7,4, $lang['G'], 'B', 0,'C');					$pdf->Cell(7,4, $lang['N'], 'B', 0,'C');					$pdf->Cell(7,4, $lang['P'], 'B', 0,'C');					if($arrayCompetition['Points'] == '4-2-1-0')						$pdf->Cell(7,4, $lang['F'], 'B', 0,'C');					$pdf->Cell(8,4, "+", 'B', 0,'C');					$pdf->Cell(8,4, "-", 'B', 0,'C');					$pdf->Cell(8,4, "+/-", 'B', 1,'C');				}				$pts = $row['Pts_publi'];				$len = strlen($pts);				if ($len > 2)				{					if (substr($pts, $len-2, 2) == '00')						$pts = substr($pts, 0, $len-2);					else						$pts = substr($pts, 0, $len-2).'.'.substr($pts, $len-2, 2);				}				if($row['J_publi'] != '0'){					$pdf->SetFont('Arial','',9);					if($arrayCompetition['Points'] == '4-2-1-0')						$pdf->Cell(26,4, '', 0, 0,'C');					else						$pdf->Cell(30,4, '', 0, 0,'C');					$pdf->Cell(61,4, $row['Clt_publi'].'. '.$row['Libelle'], 'B', 0,'L');					$pdf->Cell(8,4, $pts, 'B','0','C');					$pdf->Cell(7,4, $row['J_publi'], 'B', 0,'C');					$pdf->Cell(7,4, $row['G_publi'], 'B', 0,'C');					$pdf->Cell(7,4, $row['N_publi'], 'B', 0,'C');					$pdf->Cell(7,4, $row['P_publi'], 'B', 0,'C');					if($arrayCompetition['Points'] == '4-2-1-0')						$pdf->Cell(7,4, $row['F_publi'], 'B', 0,'C');					$pdf->Cell(8,4, $row['Plus_publi'], 'B', 0,'C');					$pdf->Cell(8,4, $row['Moins_publi'], 'B', 0,'C');					$pdf->Cell(8,4, $row['Diff_publi'], 'B', 1,'C');				}			}		}					$pdf->SetFont('Arial','I',8);		$pdf->SetXY(165, 270);		if(isset($lang) && isset($langue['EN']) && $lang == $langue['EN'])			$pdf->Write(4, date('Y-m-d H:i'));		else			$pdf->Write(4, date('d/m/Y à H:i'));		$pdf->Output('Classement par phase '.$codeCompet,'I');	}}$page = new FeuilleCltNiveauPhase();?>