<?phpinclude_once('../commun/MyPage.php');include_once('../commun/MyBdd.php');include_once('../commun/MyTools.php');define('FPDF_FONTPATH','font/');require('../fpdf/fpdf.php');// Gestion de la Feuille de Classement par Journeeclass FeuilleCltNiveauPhase extends MyPage	 {		function FeuilleCltNiveauPhase()	{		MyPage::MyPage();		$myBdd = new MyBdd();				$codeCompet = utyGetSession('codeCompet', '');		$codeSaison = utyGetSaison();		$arrayCompetition = $myBdd->GetCompetition($codeCompet, $codeSaison);		$titreCompet = 'Compétition : '.$arrayCompetition['Libelle'].' ('.$codeCompet.')';		$qualif = $arrayCompetition['Qualifies'];		$elim = $arrayCompetition['Elimines'];		$sponsor = str_replace('http://www.kayak-polo.info/','../',$arrayCompetition['SponsorLink']);		$logo = str_replace('http://www.kayak-polo.info/','../',$arrayCompetition['LogoLink']);		//Saison		$titreDate = "Saison ".$codeSaison;		// Langue		$langue = parse_ini_file("../commun/MyLang.ini", true);		if($_GET['lang'] == 'EN')			$arrayCompetition['En_actif'] = 'O';		elseif($_GET['lang'] == 'FR')			$arrayCompetition['En_actif'] = '';					if($arrayCompetition['En_actif'] == 'O')			$lang = $langue[EN];		else			$lang = $langue[FR];		//Création		$pdf = new FPDF('L');		$pdf->Open();		$pdf->SetTitle("Classement par phase");				$pdf->SetAuthor("kayak-polo.info");		$pdf->SetCreator("kayak-polo.info");		$pdf->AddPage();		if($arrayCompetition['Sponsor_actif'] == 'O' && $sponsor != '')			$pdf->SetAutoPageBreak(true, 30);		else			$pdf->SetAutoPageBreak(true, 15);		// logo		if($arrayCompetition['Kpi_ffck_actif'] == 'O')		{			$pdf->Image('../css/banniere1.jpg',10,8,72,15,'jpg',"http://www.kayak-polo.info");			$pdf->Image('../img/ffck2.jpg',242,8,0,15,'jpg',"http://www.ffck.org");		}		if($arrayCompetition['Logo_actif'] == 'O' && $logo != '')  //&& file_exists($logo)		{			$size = getimagesize($logo);			$largeur=$size[0];			$hauteur=$size[1];			$ratio=20/$hauteur;	//hauteur imposée de 20mm			$newlargeur=$largeur*$ratio;			$posi=149-($newlargeur/2);	//210mm = largeur de page			$pdf->image($logo, $posi, 8, 0,20);		}		if($arrayCompetition['Sponsor_actif'] == 'O' && $sponsor != '')  //&& file_exists($sponsor)		{			$size = getimagesize($sponsor);			$largeur=$size[0];			$hauteur=$size[1];			$ratio=16/$hauteur;	//hauteur imposée de 16mm			$newlargeur=$largeur*$ratio;			$posi=149-($newlargeur/2);	//210mm = largeur de page			$pdf->image($sponsor, $posi, 180, 0,16);		}		// titre		$pdf->Ln(22);		$pdf->SetFont('Arial','B',14);		if($arrayCompetition['Titre_actif'] == 'O')			$pdf->Cell(273,5,$arrayCompetition['Libelle'],0,1,'C');		else			$pdf->Cell(273,5,$arrayCompetition['Soustitre'],0,1,'C');		if($arrayCompetition['Soustitre2'] != '')				$pdf->Cell(273,5,$arrayCompetition['Soustitre2'],0,1,'C');		$pdf->Ln(2);		$pdf->SetFont('Arial','BI',10);		$pdf->Cell(273,5,$lang['CLASSEMENT_PAR_PHASE'],0,0,'C');		$pdf->Ln(6);		// données		$myBdd = new MyBdd();				$sql  = "Select a.Id, a.Libelle, a.Code_club, ";		$sql .= "b.Id_journee, b.Clt, b.Pts, b.J, b.G, b.N, b.P, b.F, b.Plus, b.Moins, b.Diff, b.PtsNiveau, b.CltNiveau, ";		$sql .= "c.Phase, c.Niveau, c.Lieu ";		$sql .= "From gickp_Competitions_Equipes a, ";		$sql .= "gickp_Competitions_Equipes_Journee b Join gickp_Journees c On (b.Id_journee = c.Id) ";		$sql .= "Where a.Id = b.Id ";		$sql .= "And c.Code_competition = '";		$sql .= $codeCompet;		$sql .= "' And c.Code_saison = '";		$sql .= utyGetSaison();		$sql .= "' Order By c.Niveau, c.Phase Asc, c.Date_debut Asc, c.Lieu ASC, b.Clt Asc, b.Diff Desc, b.Plus Desc ";	 		$result = mysql_query($sql, $myBdd->m_link) or die ("Erreur Load");		$num_results = mysql_num_rows($result);		$idJournee = 0;		$idNiveau = 1;		for ($i=0;$i<$num_results;$i++)		{			$row = mysql_fetch_array($result);			$y=$pdf->GetY();			if (stristr($row['Phase'], 'group') === FALSE && stristr($row['Phase'], 'poule') === FALSE)			{				if ($row['Id_journee'] != $idJournee)				{					$idJournee = $row['Id_journee'];					if ($row['Niveau'] != $idNiveau || $y > 140)					{						$x=$pdf->GetX();						$x += 50;						if ($x > 240)						{							$pdf->AddPage();							$pdf->SetLeftMargin(10);							$pdf->SetXY(10, 50);						}else{							$pdf->SetLeftMargin($x);							$pdf->SetXY($x, 50);						}					}					$idNiveau = $row['Niveau']; 					$pdf->Ln(4);					$pdf->SetFont('Arial','B',9);					$pdf->Cell(46,4,$row['Phase'], 0,1,'C');					$pdf->SetFont('Arial','B',7);											$sql2  = "Select m.ScoreA, m.ScoreB, ce1.Libelle EquipeA, ce2.Libelle EquipeB ";					$sql2 .= "From gickp_Matchs m left outer join gickp_Competitions_Equipes ce1 on (m.Id_equipeA = ce1.Id) left outer join gickp_Competitions_Equipes ce2 on (m.Id_equipeB = ce2.Id) ";					$sql2 .= "Where m.Id_journee = ".$row['Id_journee']; 					$result2 = mysql_query($sql2, $myBdd->m_link) or die ("Erreur Load 2<br>".$sql2);					$num_results2 = mysql_num_rows($result2);					for ($j=0;$j<$num_results2;$j++)					{						$y2=$pdf->GetY();						if ($y2 > 160)						{							$x=$pdf->GetX();							$x += 50;							if ($x > 240)							{								$pdf->AddPage();								$pdf->SetLeftMargin(10);								$pdf->SetXY(10, 50);							}else{								$pdf->SetLeftMargin($x);								$pdf->SetXY($x, 58);							}						}						$row2 = mysql_fetch_array($result2);						$pdf->Ln(4);						if($row2['ScoreA'] > $row2['ScoreB'])							$pdf->SetFont('Arial','B',8);						else							$pdf->SetFont('Arial','',8);						$pdf->Cell(40, 4, $row2['EquipeA'], 1, 0,'L');						$pdf->Cell(6, 4, $row2['ScoreA'], 1, 1,'R');						if($row2['ScoreA'] < $row2['ScoreB'])							$pdf->SetFont('Arial','B',8);						else							$pdf->SetFont('Arial','',8);						$pdf->Cell(40, 4, $row2['EquipeB'], 1, 0,'L');						$pdf->Cell(6, 4, $row2['ScoreB'], 1, 1,'R');					}				}			}			else			{				$idEquipe = $row['Id'];				if ($row['Id_journee'] != $idJournee)				{					$idJournee = $row['Id_journee'];					if ($row['Niveau'] != $idNiveau || $y > 140)					{						$x=$pdf->GetX();						$x += 60;						if ($x > 240)						{							$pdf->AddPage();							$pdf->SetLeftMargin(10);							$pdf->SetXY(10, 50);						}else{							$pdf->SetLeftMargin($x);							$pdf->SetXY($x, 50);						}					}					$idNiveau = $row['Niveau']; 					$pdf->Ln(4);					$pdf->SetFont('Arial','B',9);					$pdf->Cell(46,4,$row['Phase'], 0,1,'C');					/*	$pdf->SetFont('Arial','B',8);						$pdf->Cell(10,5, "Pts", 'LTBR','0','C');						$pdf->Cell(10,5, "J", 'LTBR','0','C');						$pdf->Cell(10,5, "G", 'LTBR','0','C');						$pdf->Cell(10,5, "N", 'LTBR','0','C');						$pdf->Cell(10,5, "P", 'LTBR','0','C');						$pdf->Cell(10,5, "F", 'LTBR','0','C');						$pdf->Cell(10,5, "Plus", 'LTBR','0','C');						$pdf->Cell(10,5, "Moins", 'LTBR','0','C');						$pdf->Cell(10,5, "Diff", 'LTBR','1','C');					*/				}				$pts = $row['Pts'];				$len = strlen($pts);				if ($len > 2)				{					if (substr($pts, $len-2, 2) == '00')						$pts = substr($pts, 0, $len-2);					else						$pts = substr($pts, 0, $len-2).'.'.substr($pts, $len-2, 2);				}								$pdf->SetFont('Arial','',7);				$pdf->Cell(6, 4, $row['Clt'], 1,'0','C');				$pdf->Cell(40,4, $row['Libelle'],1,1,'C');				/*				$pdf->Cell(10,5, $pts, 'LTBR','0','C');				$pdf->Cell(10,5, $row['J'], 'LTBR','0','C');				$pdf->Cell(10,5, $row['G'], 'LTBR','0','C');				$pdf->Cell(10,5, $row['N'], 'LTBR','0','C');				$pdf->Cell(10,5, $row['P'], 'LTBR','0','C');				$pdf->Cell(10,5, $row['F'], 'LTBR','0','C');				$pdf->Cell(10,5, $row['Plus'], 'LTBR','0','C');				$pdf->Cell(10,5, $row['Moins'], 'LTBR','0','C');				$pdf->Cell(10,5, $row['Diff'], 'LTBR','1','C');				*/			}		}					$pdf->Output('Classement par phase '.$codeCompet.'.pdf','I');	}}$page = new FeuilleCltNiveauPhase();